import { ADD_PRODUCT_BASKET, GET_NUMBERS_BASKET } from "../actions/types";
import axios from "axios";

const initialState = {
  basketNumbers: 0,
  cartCost: 0,
  products: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD_PRODUCT_BASKET:
      console.log("state prod", state.products);
      let addProduct = { ...state.products.push(action.payload) };
      return {
        basketNumbers: state.basketNumbers + 1,
        products: addProduct,
      };
    case GET_NUMBERS_BASKET:
      return {
        ...state,
      };
    default:
      return state;
  }
};
