import { ADD_PRODUCT_BASKET } from "./types";

export const addToBasket = (productName) => {
  return (dispatch) => {
    console.log("Adding to Basket", productName);
    dispatch({
      type: ADD_PRODUCT_BASKET,
      payload: productName,
    });
  };
};
