import React, { useState, Component, useEffect } from "react";
import HomeStyle from "./style";
import axios from "axios";
import { getPizzas } from "../../actions";
import pizzaImg from "../../assets/images/pizza.png";
import SVGInline from "react-svg-inline";
import Navbar from "../../components/Navbar";
import { cart } from "../../assets/icons";

const Home = () => {
  const [pizzas, setPizzas] = useState([]);
  const [basketNumbers, setBasketNumbers] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      let result = await axios.get(`http://localhost:8000/api/pizzas`);

      setPizzas(result.data);
    };
    fetchData();
  }, []);

  const addToBasket = () => {
    setBasketNumbers(basketNumbers + 1);
  };

  return (
    <>
      <Navbar />
      <h1>Current Numbers in cart: {basketNumbers}</h1>
      <HomeStyle>
        {pizzas.length !== undefined
          ? pizzas.map((val, key) => (
              <div className="pizza-single" key={key}>
                <div className="pizza-img">
                  <img src={pizzaImg} alt="pizza-img" />
                </div>
                <div className="pizza-description">
                  <p className="pizza-name">{val.pizza_name}</p>
                  <p className="pizza-desc">{val.pizza_description}</p>
                  <div className="price">
                    <p className="pizza-price">{val.pizza_price}0 $</p>
                    <div className="btn btn-secondary" onClick={addToBasket}>
                      Buy <SVGInline svg={cart} />
                    </div>
                  </div>
                </div>
              </div>
            ))
          : "No Pizzas!"}
      </HomeStyle>
    </>
  );
};

export default Home;
