import React from "react";
import axios from "axios";

export const getPizzas = () => async () => {
  try {
    let request = await axios.get(`http://localhost:8000/api/pizzas`);
    return request.data;
  } catch (error) {
    if (error.response.status === 400) {
      return error.response.data;
    }
  }
};
