import React from "react";
import CartStyle from "./style";
import { connect } from "react-redux";

const Cart = ({ basketProps }) => {
  console.log("basketProps", basketProps);

  let productsInCart = [];

  Object.keys(basketProps.products).forEach(function (item) {
    console.log(item);
    console.log(basketProps.products[item].inCart);
    if (basketProps.products[item].inCart) {
      productsInCart.push(basketProps.products[item]);
    }

    console.log("products in cart", productsInCart);
  });

  productsInCart = productsInCart.map((val, index) => {
    return (
      <div className="products" key={index}>
        <p>{val.pizza_name}</p>
        <p>{val.pizza_price}</p>
        <p>{val.pizza_description}</p>
        <p>{val.numbers}</p>
      </div>
    );
  });
  return (
    <CartStyle>
      <p>Cart</p>

      {console.log("produtsInCart", productsInCart)}
      {productsInCart.length > 0
        ? productsInCart.map((val, key) => {
            console.log("val", val);
            return (
              <div className="pizza-single" key={key}>
                <div className="pizza-img">
                  <img src={`../images/${val.pizza_image}`} alt="pizza-img" />
                </div>
                <div className="pizza-description">
                  <p className="pizza-name">{val.pizza_name}</p>
                  <p className="pizza-desc">{val.pizza_description}</p>
                  <div className="price">
                    <p className="pizza-price">{val.pizza_price}0 $</p>
                  </div>
                </div>
              </div>
            );
          })
        : null}
    </CartStyle>
  );
};

const mapStateToProps = (state) => ({
  basketProps: state.basketState,
});

export default connect(mapStateToProps)(Cart);
