import React, { Component } from "react";
import HomeStyle from "./style";
import axios from "axios";
import { getPizzas } from "../../actions";

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      pizzas: [],
    };
  }

  componentDidMount = async () => {
    // let pizzas = await axios.get(`http://localhost:8000/api/pizzas`);
    // this.setState({ pizzas: pizzas.data });

    let data = await getPizzas();
    console.log("test", data);
    this.setState({ pizzas: data });
  };

  render() {
    return (
      <HomeStyle>
        {this.state.pizzas.length !== 0
          ? this.state.pizzas.map((val, key) => (
              <div className="pizzas" key={key}>
                <div className="pizza-img">IMG</div>
                <div class="pizza-description">
                  <p>Name: {val.pizza_name}</p>
                  <p>Description: {val.pizza_description}</p>
                  <p>Price: {val.pizza_price}</p>
                </div>
                <div class="pizza-btns">Buttons</div>
              </div>
            ))
          : "No users!"}
      </HomeStyle>
    );
  }
}
