import React, { useState, Component, useEffect, Fragment } from "react";
import CartStyle from "./style";
import axios from "axios";
import pizzaImg from "../../assets/images/pizza.png";
import SVGInline from "react-svg-inline";
import Navbar from "../../components/Navbar";
import { cart } from "../../assets/icons";
import { connect } from "react-redux";
import { addToBasket } from "../../actions/addAction";

const Cart = ({ basketProps }) => {
  console.log("basketProps", basketProps);

  let productsInCart = [];

  Object.keys(basketProps.products).forEach(function (item) {
    console.log(item);
    console.log(basketProps.products[item].inCart);
    if (basketProps.products[item].inCart) {
      productsInCart.push(basketProps.products[item]);
    }

    console.log("products in cart", productsInCart);
  });

  productsInCart = productsInCart.map((val, index) => {
    return (
      <div className="products" key={index}>
        <p>{camelize(val.pizza_name)}</p>
        <p>{val.pizza_price}</p>
        <p>{val.pizza_description}</p>
        <p>{val.numbers}</p>
      </div>
    );
  });
  return (
    <CartStyle>
      <p>Ideees</p>
      {productsInCart}
    </CartStyle>
  );
};

const mapStateToProps = (state) => ({
  basketProps: state.basketState,
});

export default connect(mapStateToProps)(Cart);
