import styled from "styled-components";

const CartStyle = styled.div`
  padding: 200px 500px;
  background: #f4f1ef;
  min-height: 100vh;

  .product {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 15px 0px;

    .pizza-img {
      flex-basis: 20%;
      background: #fff;
      border: 1px solid #000;

      img {
        width: 100%;
        min-height: 160px;
      }
    }

    .pizza-description {
      padding-left: 20px;
      flex-basis: 50%;
      border: 1px solid #000;
      min-height: 160px;

      .pizza-name {
        font-size: 18px;
        font-weight: 600;
      }
    }
    .pizza-price {
      flex-basis: 50%;
      border: 1px solid #000;
      min-height: 160px;
    }
  }
`;

export default CartStyle;
