import styled from "styled-components";

const HomeStyle = styled.div`
  padding: 50px 400px;
  border: 1px solid #000;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;

  .pizza-single {
    flex-basis: 25%;
    padding: 20px;
    height: 345px;
    margin: 10px;
    display: flex;
    border: 2px solid #c4c4c4;
    border-radius: 10px;
  }
`;

export default HomeStyle;
