import React, { Component } from "react";
import HomeStyle from "./style";
import axios from "axios";
import { getPizzas } from "../../actions";
import pizzaImg from "../../assets/images/pizza.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCart } from "@fortawesome/free-solid-svg-icons";

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      pizzas: [],
    };
  }

  componentDidMount = async () => {
    let pizzas = await axios.get(`http://localhost:8000/api/pizzas`);
    this.setState({ pizzas: pizzas.data });
  };

  render() {
    return (
      <HomeStyle>
        {this.state.pizzas.length !== 0
          ? this.state.pizzas.map((val, key) => (
              <div className="pizza-single" key={key}>
                <div className="pizza-img">
                  <img src={pizzaImg} alt="pizza-img" />
                </div>
                <div className="pizza-description">
                  <p className="pizza-name">{val.pizza_name}</p>
                  <p className="pizza-desc">{val.pizza_description}</p>
                  <div className="price">
                    <p className="pizza-price">{val.pizza_price}0 $</p>
                    <div className="btn btn-primary">
                      Buy <FontAwesomeIcon icon={FiShoppingCart} />
                    </div>
                  </div>
                </div>
              </div>
            ))
          : "No users!"}
      </HomeStyle>
    );
  }
}
