import React, { useEffect, useState } from "react";
import axios from "axios";
import NavbarStyle from "./style";
import { connect } from "react-redux";
import { getBasketNumbers } from "../../actions/getAction";
import { Link, withRouter } from "react-router-dom";
import { cart } from "../../assets/icons";
import SVGInline from "react-svg-inline";
import AuthService from "../../services/auth-service";

const Navbar = (props) => {
  const user = AuthService.getCurrentUser();
  let token;
  if (localStorage.getItem("user")) {
    token = localStorage.getItem("user").access_token;
  } else {
    token = "";
  }
  const [userData, setUserData] = useState([]);
  useEffect(() => {
    getBasketNumbers();
    const fetchData = async () => {
      let result = await axios.get(`http://localhost:8000/api/auth/me`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("user").access_token}`,
        },
      });
      setUserData(result.data);
    };
    fetchData();
  }, []);

  const logOut = () => {
    AuthService.logout();
  };

  useEffect(() => {
    getBasketNumbers();
  }, []);
  return (
    <NavbarStyle>
      {console.log("LS", localStorage.getItem("user"))}
      <div className="logo">
        <Link to="/">
          <img src={`../logo192.png`} alt="pizza-img" />
        </Link>
      </div>
      <div className="cart-container">
        {console.log("etf", userData)}

        <div className="cart btn btn-primary">
          <Link to="/cart">
            <SVGInline svg={cart} />{" "}
            <p>( {props.basketProps.basketNumbers} )</p>
          </Link>
        </div>
        <div className="sign-up">
          {user ? (
            <Link to="/login" onClick={logOut}>
              Logout
            </Link>
          ) : (
            <>
              <Link to="/register">Register</Link>
              <Link to="/login">Login</Link>
            </>
          )}
        </div>
      </div>
    </NavbarStyle>
  );
};

const mapStateToProps = (state) => ({
  basketProps: state.basketState,
});

export default withRouter(
  connect(mapStateToProps, { getBasketNumbers })(Navbar)
);
