import styled from "styled-components";

const NotificationStyle = styled.div`
  .alert {
    color: white;
    border-radius: 10px;
    position: absolute;
    top: 50px;
    right: 10px;
    padding: 20px;
    display: flex;
    align-items: center;
    z-index: 1111;
  }
  .alert p {
    margin: 0;
  }
  .alert-error {
    background: lightcoral;
  }
  .alert-success {
    background: lightgreen;
  }
  .close {
    color: white;
    cursor: pointer;
    margin-right: 10px;
  }
`;

export default NotificationStyle;
